import { useEffect } from "react";
import Scrollbar from 'smooth-scrollbar';
import { Link} from "react-scroll";

var options = {
    damping: 0.2,
    continuousScrolling: false,
    renderByPixels: true,
};

const Scroll = () => {
    useEffect(() => {  
        Scrollbar.initAll(document.querySelector('#root'), options);
    }, [])
    
    return null;
}

export default Scroll;
