import { useEffect } from "react";
import Scrollbar from 'smooth-scrollbar';

var options = {
    damping: 0.1,
    continuousScrolling: true,
    delegateTo: {onclick},
};

const Scroll = () => {
    useEffect(() => {  
        Scrollbar.init(document.body, options);
    }, [])
    
    return null;
}

export default Scroll;
